import React from "react";
import "./home.css";
import backgroundOne from "../../assets/images/background0ne.svg";
import circleBg from "../../assets/images/circleBg.svg";
import daveSeat from "../../assets/images/daveSeat.svg";
import spreadBg from "../../assets/images/spreadBg.svg";
import daveImg from "../../assets/images/daveImg.svg";
import blueySplash from "../../assets/images/blueySplash.svg";

const Home = () => {
  return (
    <div>
      <section
        className="dave-background row d-flex flex-wrap align-items-center mx-0"
        style={{ background: `url(${backgroundOne})` }}
      >
        <article className="col-md-8">
        <article
          className="dave-circle"
          style={{
            background: `url(${circleBg})`,
            height: "602px",
            width: "634px",
          }}
        >
          <div className="dave-name">
            <h3>Hi, I'm David</h3>
          </div>
        </article>
        </article>
        <article className="col-md-4">
          <img className="dave-seat" src={daveSeat} alt="David" />
        </article>
      </section>

      <img className="dave-spreadBg" src={spreadBg} alt="Spread" />
      <section id="about" className="dave-about">
          <img src={daveImg} alt="David"/>
          <article className="dave-about-article px-5">
              <h3 className="dave-heading">About Me</h3>
              <p className="dave-paragraph pb-3">An inquisitive Computer Science Engineering student, skilled in leadership, seeking to leverage solid development skills with focus on collaboration, communication and passion.</p>
              {/* <button className="dave-btn">View/Download CV</button> */}
              <a className="dave-btn" href="/resume.pdf" download="resume"  target="_blank" rel="noopener noreferrer">View/Download CV</a>
          </article>
          {/* <div className="blue-splash"><img src={blueySplash} alt="Splash" /></div> */}
      </section>

    </div>
  );
};

export default Home;
