                <a href="https://www.linkedin.com/in/david-nwakaihe-26006b174/">
                  <img className="pl-3" src={linkedin} alt="linkedin" />
                </a>
                <a href="https://www.instagram.com/don_kachi/">
                  <img className="pl-3" src={instagram} alt="instagram" />
                </a>
